from math import gcddef operate_operation(first: tuple[int, int], second: tuple[int, int],                      operator_c: chr) -> tuple[int, int]:    """    Function that calculates the result of the operation.    :param tuple[int, int] first: Tuple    with the denominator and numerator    of the rational number, which is the first operand.    :param tuple[int, int] second: Second operand.    :param chr operator_c: Operator.    :return tuple[int, int]:    Tuple with the denominator and numerator of the    rational number in result,    returns (0, 0) if something wrong with the operator.    """    if operator_c == '+':        gcd_val = gcd(first[1], second[1])        first_term = first[0] * (second[1] // gcd_val), first[1] * (                second[1] // gcd_val)        second_term = second[0] * (first[1] // gcd_val), second[1] * (                first[1] // gcd_val)        answer = normalize_fraction(            (first_term[0] + second_term[0], first_term[1]))        return answer    if operator_c == '-':        gcd_val = gcd(first[1], second[1])        first_term = first[0] * (second[1] // gcd_val), first[1] * (                second[1] // gcd_val)        second_term = second[0] * (first[1] // gcd_val), second[1] * (                first[1] // gcd_val)        answer = first_term[0] - second_term[0], first_term[1]        answer = normalize_fraction(answer)        return answer    if operator_c == '*':        numerator = first[0] * second[0]        denominator = first[1] * second[1]        answer = normalize_fraction((numerator, denominator))        return answer    if operator_c == '/':        numerator = first[0] * second[1]        denominator = first[1] * second[0]        if denominator < 0:            numerator *= -1            denominator *= -1        answer = normalize_fraction((numerator, denominator))        return answer    return 0, 0def normalize_fraction(number: tuple[int, int]) -> tuple[int, int]:    """    Function that converts a common fraction to the correct form.    :param tuple[int, int] number: Tuple containing the numerator and    denominator of a rational number.    :return tuple[int, int]: Returns the normalized tuple of the number.    """    greatest_common_divisor = gcd(*number)    return number[0] // greatest_common_divisor, number[        1] // greatest_common_divisordef convert_to_string(number: tuple[int, int]) -> str:    """    Function that converts a rational number from tuple to string.    :param int number: Tuple to convert.    :return str: Converted number.    """    if abs(number[0]) > number[1] != 1:        if number[0] >= 0:            return (str(number[0] // number[1]) + ' ' + str(                abs(number[0]) % number[1])                    + '/' + str(number[1]))        return (str(-(abs(number[0]) // number[1])) + ' ' + str(            abs(number[0]) % number[1])                + '/' + str(number[1]))    elif number[1] == 1:        return str(number[0])    return str(number[0]) + '/' + str(number[1])def get_fraction_from_string(string: str) -> tuple[int, int]:    """    Function that returns a tuple with the numerator and denominator    of a rational number from an input string.    :param str string: String to convert to a number.    :return tuple[int, int]:    Tuple representation of the rational number.    """    sign = 1    if string[0] == '-':        sign = -1    nums = string.split()    if len(nums) == 2:        numerator, denominator = map(int, nums[1].split('/'))        st = int(nums[0])        numerator = sign * numerator + st * denominator        return numerator, denominator    elif '/' in nums[0]:        numerator, denominator = map(int, nums[0].split('/'))        return numerator, denominator    return int(nums[0]), 1first_op = input()op = input()second_op = input()first_n = get_fraction_from_string(first_op)second_n = get_fraction_from_string(second_op)print(convert_to_string(operate_operation(first_n, second_n, op)))