from collections import namedtuple, OrderedDict, defaultdict, UserDict# Создаём именованные кортежи для русских и французских слов# Именованные кортежи позволяют# создавать типизированные структуры данных,# где каждый элемент имеет своё имя.# Они удобны для хранения записей с фиксированным набором полей.RussianWord = namedtuple('RussianWord', ['word', 'count'])FrenchWord = namedtuple('FrenchWord', ['word', 'count'])# Пример использования именованного кортежа:print("Пример использования именованного кортежа для русских слов:")for word, count in bottom_5_russian_words:    russian_word = RussianWord(word=word, count=count)    print(russian_word)print("Пример использования именованного кортежа для французских слов:")for word, count in top_words:    french_word = FrenchWord(word=word, count=count)    print(french_word)# 4. Сравнение типов данных словарей# Обычный словарьnormal_dict = {'a': 1, 'b': 2, 'c': 3}# OrderedDict - сохраняет порядок элементов в том порядке,# в котором они были добавлены.ordered_dict = OrderedDict([('a', 1), ('b', 2), ('c', 3)])# defaultdict - автоматически создаёт# новое значение, если ключ отсутствует.default_dict = defaultdict(int)default_dict['a'] = 1default_dict['b'] += 1# UserDict - обёртка над обычным# словарём для удобного доступа к элементам.user_dict = UserDict({'a': 1, 'b': 2, 'c': 3})# Демонстрация различий между типами словарейprint("Обычный словарь:", normal_dict)print("OrderedDict:", ordered_dict)print("defaultdict:", default_dict)print("UserDict:", user_dict)